import React, { Component } from 'react';
import { Redirect } from 'react-router-dom';
import { withStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardMedia from '@material-ui/core/CardMedia';
import CardContent from '@material-ui/core/CardContent';
import Typography from '@material-ui/core/Typography';
import CardActions from '@material-ui/core/CardActions';
import dateParse from './dateBrFormat';

const styles = muiBaseTheme => ({
  card: {
    maxWidth: 600,
    overflow: 'auto',

    margin: 'auto',
    padding: 10,
    transition: '0.3s',
    boxShadow: '0 8px 40px -12px rgba(0,0,0,0.3)',
    '&:hover': {
      boxShadow: '0 16px 70px -12.125px rgba(0,0,0,0.3)'
    }
  },
  media: {
    paddingTop: '56.25%',
    marginTop: '30',
    height: 0
  },
  content: {
    textAlign: 'justify',
    padding: muiBaseTheme.spacing.unit * 3
  },
  divider: {
    margin: `${muiBaseTheme.spacing.unit * 3}px 0`
  },
  heading: {
    fontWeight: 'bold'
  },
  subheading: {
    lineHeight: 1.8
  },
  pos: {
    marginBottom: 12,
    textAlign: 'center'
  },
  text: {
    textAlign: 'center'
  }
});

export class postPreview extends Component {
  handleClick = () => {
    const { post } = this.props;
    this.setState({ idRedirect: post.id, redirect: true });
  };

  state = {
    idRedirect: '',
    redirect: false
  };
  render() {
    const { post, classes } = this.props;
    const { idRedirect, redirect } = this.state;
    if (redirect) {
      return <Redirect to={`/pet/posts/${idRedirect}`} />;
    }
    return (
      <div className="App">
        <Card className={classes.card}>
          <div className="hoverOn">
            {post.foto ? (
              <CardMedia
                onMouseEnter={() => {
                  document.body.style.cursor = 'pointer';
                }}
                onMouseLeave={() => {
                  document.body.style.cursor = 'default';
                }}
                onClick={this.handleClick}
                className={classes.media}
                image={post.foto}
              />
            ) : (
              <CardMedia
                onMouseEnter={() => {
                  document.body.style.cursor = 'pointer';
                }}
                onMouseLeave={() => {
                  document.body.style.cursor = 'default';
                }}
                onClick={this.handleClick}
                className={classes.media}
                image={
                  'https://image.freepik.com/free-photo/river-foggy-mountains-landscape_1204-511.jpg'
                }
              />
            )}

            <CardContent>
              <Typography
                className={'hoverOn--on ' + classes.text}
                gutterBottom
                variant={'h3'}
                onMouseEnter={() => {
                  document.body.style.cursor = 'pointer';
                }}
                onMouseLeave={() => {
                  document.body.style.cursor = 'default';
                }}
                onClick={this.handleClick}
              >
                <h4>{post.titulo}</h4>
              </Typography>
              <Typography className={classes.pos} color="textSecondary">
                {dateParse(post.data)}
              </Typography>

              <Typography
                className={
                  ' hoverOn--on MuiTypography--subheading ' + classes.content
                }
                variant={'p'}
              >
                {post.descricao.length > 400
                  ? post.descricao.substring(0, 400) + '...'
                  : post.descricao}
              </Typography>
            </CardContent>
            <CardActions
              className={classes.content}
              disableSpacing
            ></CardActions>
          </div>
        </Card>
      </div>
    );
  }
}
export default withStyles(styles)(postPreview);
