import { connect } from "react-redux";
import { change, submit, isSubmitting } from "redux-form";
import React, { Component, Fragment } from "react";
import {
  List,
  Link,
  DateField,
  Datagrid,
  Button,
  withDataProvider,
} from "react-admin";
import Typography from "@material-ui/core/Typography";
import IconContentAdd from "@material-ui/icons/DateRange";
import Dialog from "@material-ui/core/Dialog";
import DialogTitle from "@material-ui/core/DialogTitle";
import PropTypes from "prop-types";
import DialogContent from "@material-ui/core/DialogContent";

class PostQuickCreateButton extends Component {
  state = {
    error: false,
    showDialog: false,
    days: null
  };

  handleClick = () => {
    this.setState({ showDialog: true });
  };

  handleCloseClick = () => {
    this.setState({ showDialog: false });
  };

  handleSaveClick = () => {
    const { submit } = this.props;

    // Trigger a submit of our custom quick create form
    // This is needed because our modal action buttons are oustide the form
    submit("post-quick-create");
  };

  handleSubmit = values => {};

  render() {
    const { showDialog } = this.state;
    // const { isSubmitting, basePath } = this.props;
    const LinkToRelatedPost = ({ record }) => (
      <Link to={`/Dias/${record.id}/show`}>
        <Typography variant="caption" color="inherit" align="right">
          Visualizar lista de presen√ßa
        </Typography>
      </Link>
    );

    return (
      <Fragment>
        <Button onClick={this.handleClick} label="Visualizar dias">
          <IconContentAdd />
        </Button>
        <Dialog
          fullWidth
          open={showDialog}
          onClose={this.handleCloseClick}
          aria-label="Visualizar dias"
        >
          <DialogTitle>Dias</DialogTitle>
          <DialogContent>
            <List
              basePath="Dias"
              resource="Dias"
              actions={false}
              bulkActionButtons={false}
              location=""
              filter={{ turmaId: this.props.turmId }}
              {...this.props}
            >
              <Datagrid>
                <DateField source="dia" />
                <LinkToRelatedPost />
              </Datagrid>
            </List>
          </DialogContent>
        </Dialog>
      </Fragment>
    );
  }
}

PostQuickCreateButton.propTypes = {
  dataProvider: PropTypes.func.isRequired,

  dispatch: PropTypes.func.isRequired,
  record: PropTypes.object
};
const mapStateToProps = state => ({
  isSubmitting: isSubmitting("post-quick-create")(state)
});

const mapDispatchToProps = {
  change,

  submit
};
export default connect(
  mapStateToProps,
  mapDispatchToProps
)(withDataProvider(PostQuickCreateButton));
