import React, { Component, Fragment } from 'react';
import Axios from 'axios';
import PathName from '../pathConst';
import Header from '../Header/Header';
import CardTurmaItem from './turmaItemWithCard';
import TurmaItem from './turmaItemWithoutCard';

class atividadeItem extends Component {
  componentWillMount() {
    const { atividadeId } = this.props;
    const filterAtividade = {
      include: {
        relation: 'categoria'
      }
    };
    Axios.get(
      `http://${PathName}/pet/api/Atividades/${atividadeId}?filter=${JSON.stringify(
        filterAtividade
      )}`
    ).then(res => {
      this.setState({
        nome: res.data.nome,
        descricao: res.data.descricao,
        cargaHoraria: res.data.cargaHoraria,
        valor: res.data.valor,
        categoria: res.data.categoria.nome
      });
    });
    Axios.get(
      `http://${PathName}/pet/api/Atividades/${atividadeId}/turmas`
    ).then(res => {
      this.setState({ turmas: res.data });
    });
  }

  state = {
    nome: '',
    descricao: '',
    cargaHoraria: '',
    valor: '',
    categoria: '',
    turmas: []
  };

  formatValor = valor => {
    return valor === 0 ? 'Gratuito' : `R$ ${valor}`;
  };

  render() {
    const {
      nome,
      descricao,
      cargaHoraria,
      valor,
      turmas,
      categoria
    } = this.state;
    const { eventoId } = this.props;
    return (
      <Fragment>
        <Header />
        <div className="container">
          <div className="card-panel z-depth-2">
            <div className="row">
              <div className="col s12">
                <h3 style={{ textAlign: 'center' }}>{nome}</h3>

                <h6
                  style={{ textAlign: 'justify' }}
                  className="basicInfo grey-text text-darken-2"
                >
                  {descricao}
                </h6>
                <hr className="style-six" />
                <div className="basicInfo grey-text text-darken-2">
                  <p>
                    <b>Categoria:</b>
                    <i className="material-icons left">timeline</i>
                    <span className="right">{categoria}</span>
                  </p>
                  <hr className="style-six" />
                  <p>
                    <b>Carga horaria:</b>
                    <i className="material-icons left">access_time</i>
                    <span className="right">{cargaHoraria}</span>
                  </p>
                  <hr className="style-six" />
                  <p>
                    <b>Valor:</b>
                    <i className="material-icons left">attach_money</i>
                    <span className="right">{this.formatValor(valor)}</span>
                  </p>
                  <hr className="style-six" />
                  {turmas.length > 1 ? (
                    <div>
                      <h5 style={{ textAlign: 'left' }}>Turmas </h5>
                      {turmas.map((turma, i) => (
                        <CardTurmaItem
                          turma={turma}
                          key={i}
                          eventoId={eventoId}
                        />
                      ))}
                    </div>
                  ) : (
                    turmas.map((turma, i) => (
                      <TurmaItem turma={turma} key={i} eventoId={eventoId} />
                    ))
                  )}
                </div>
              </div>
            </div>
          </div>
        </div>
      </Fragment>
    );
  }
}

export default atividadeItem;
